select distinct T2.CD_CIR_DENTISTA, T2.SENHA, (select T1.CD_ASSOCIADO from TBIA_TEMP_AUTOMACAO_PRE T1 where T1.CD_CIR_DENTISTA = T2.CD_CIR_DENTISTA and rownum <= 1) CD_ASSOCIADO, ( select T1.CD_EVENTO from TBIA_TEMP_AUTOMACAO_PRE T1 where T1.CD_CIR_DENTISTA = T2.CD_CIR_DENTISTA and T1.CD_ASSOCIADO = T2.CD_ASSOCIADO and T1.CD_EVENTO = T2.CD_EVENTO and rownum <= 1) CD_EVENTO, T2.ID_PROTOCOLO from TBIA_TEMP_AUTOMACAO_PRE T2 where rownum <= 10000