SELECT DISTINCT a.CD_ASSOCIADO, e.ID_EMPRESA_PF, CASE WHEN u.USER_PASSWORD is null THEN 'PRIMEIRO ACESSO' WHEN u.USER_PASSWORD is not null THEN FU_HASH_LOGIN_DECRIPTA(u.USER_PASSWORD)END AS USER_PASSWORD FROM ADMPROD.TBOD_ASSOCIADO a LEFT JOIN TBOD_USER u ON a.CD_ASSOCIADO = u.USER_LOGIN INNER JOIN ADMPROD.TBOD_EMPRESA e ON a.CD_EMPRESA = e.CD_EMPRESA INNER JOIN ADMPROD.TBOD_MARCA_EMPRESA me ON e.CD_MARCA = me.CD_MARCA INNER JOIN ADMPROD.TBOD_PLANO p ON a.CD_PLANO_ATUAL = p.CD_PLANO INNER JOIN ADMPROD.TBOD_REDE_EMPRESA_PLANO r ON r.CD_PLANO = p.CD_PLANO INNER JOIN ADMPROD.TBOD_FATURAMENTO f ON a.CD_ASSOCIADO = f.CD_ASSOCIADO WHERE a.ID_INATIVO = 'S' and e.ID_EMPRESA_PF = 'S' and me.CD_MARCA = 1 and ROWNUM < 1001